mapper.select:
SELECT T_CAREWORK_RECORD.*, T_USER_CARER.name AS carer_name, T_USER_ELDER.name AS elder_name FROM (T_CAREWORK_RECORD LEFT JOIN T_USER AS T_USER_CARER ON T_CAREWORK_RECORD.carer_id = T_USER_CARER.user_id AND T_USER_CARER.user_type = #{staffType}) LEFT JOIN T_USER AS T_USER_ELDER ON T_CAREWORK_RECORD.elder_id = T_USER_ELDER.user_id AND T_USER_ELDER.user_type = #{elderType}
T_CAREWORK_RECORD.carer_id = #{carerId}
AND T_CAREWORK_RECORD.elder_id = #{elderId}
and #{startDate} <= convert(nvarchar(23), T_CAREWORK_RECORD.finish_time, 120)
and #{endDate} >= convert(nvarchar(23), T_CAREWORK_RECORD.finish_time, 120)

SELECT T_CAREWORK_RECORD.*, T_USER_CARER.name AS carer_name, T_USER_ELDER.name AS elder_name FROM (T_CAREWORK_RECORD LEFT JOIN T_USER AS T_USER_CARER ON T_CAREWORK_RECORD.carer_id = T_USER_CARER.user_id AND T_USER_CARER.user_type = #{staffType}) LEFT JOIN T_USER AS T_USER_ELDER ON T_CAREWORK_RECORD.elder_id = T_USER_ELDER.user_id AND T_USER_ELDER.user_type = #{elderType} WHERE convert(nvarchar(10), T_CAREWORK_RECORD.finish_time, 11) = convert(nvarchar(10), (SELECT TOP 1 finish_time FROM T_CAREWORK_RECORD WHERE T_CAREWORK_RECORD.elder_id = #{elderId} ORDER BY finish_time DESC), 11)
AND T_CAREWORK_RECORD.carer_id = #{carerId}
AND T_CAREWORK_RECORD.elder_id = #{elderId}
